import React, { useState, useEffect } from 'react';
import { geminiService } from '../services/geminiAPI';

const ManuscriptManager = () => {
  const [chapters, setChapters] = useState([]);
  const [activeChapter, setActiveChapter] = useState(null);
  const [manuscriptTitle, setManuscriptTitle] = useState('Untitled Manuscript');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Load manuscript data from localStorage on component mount
  useEffect(() => {
    const savedManuscript = localStorage.getItem('manuscript_data');
    if (savedManuscript) {
      const data = JSON.parse(savedManuscript);
      setChapters(data.chapters || []);
      setManuscriptTitle(data.title || 'Untitled Manuscript');
      if (data.chapters.length > 0) {
        setActiveChapter(data.chapters[0]);
      }
    }
  }, []);

  // Save manuscript data to localStorage whenever it changes
  useEffect(() => {
    const manuscriptData = {
      title: manuscriptTitle,
      chapters: chapters,
      lastModified: new Date().toISOString()
    };
    localStorage.setItem('manuscript_data', JSON.stringify(manuscriptData));
  }, [chapters, manuscriptTitle]);

  const addNewChapter = () => {
    const newChapter = {
      id: Date.now(),
      title: `Chapter ${chapters.length + 1}`,
      content: '',
      wordCount: 0,
      progress: 0,
      notes: '',
      createdAt: new Date().toISOString(),
      lastModified: new Date().toISOString()
    };

    setChapters([...chapters, newChapter]);
    setActiveChapter(newChapter);
  };

  const updateChapter = (chapterId, updates) => {
    const updatedChapters = chapters.map(chapter => {
      if (chapter.id === chapterId) {
        const wordCount = updates.content ? updates.content.split(' ').filter(w => w).length : chapter.wordCount;
        return {
          ...chapter,
          ...updates,
          wordCount,
          lastModified: new Date().toISOString()
        };
      }
      return chapter;
    });

    setChapters(updatedChapters);
    if (activeChapter && activeChapter.id === chapterId) {
      setActiveChapter({ ...activeChapter, ...updates });
    }
  };

  const deleteChapter = (chapterId) => {
    if (window.confirm('Are you sure you want to delete this chapter?')) {
      const updatedChapters = chapters.filter(chapter => chapter.id !== chapterId);
      setChapters(updatedChapters);

      if (activeChapter && activeChapter.id === chapterId) {
        setActiveChapter(updatedChapters.length > 0 ? updatedChapters[0] : null);
      }
    }
  };

  const exportManuscript = () => {
    const fullText = chapters
      .map(chapter => `${chapter.title}\n\n${chapter.content}`)
      .join('\n\n---\n\n');

    const blob = new Blob([fullText], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${manuscriptTitle.replace(/[^a-z0-9]/gi, '_').toLowerCase()}.txt`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const analyzeChapter = async (chapterId) => {
    const chapter = chapters.find(c => c.id === chapterId);
    if (!chapter || !chapter.content.trim()) return;

    setLoading(true);
    setError('');

    try {
      const response = await geminiService.analyzeChapter(chapter.content);
      if (response.success) {
        updateChapter(chapterId, {
          analysis: response.analysis,
          suggestions: response.suggestions
        });
      }
    } catch (error) {
      setError('Failed to analyze chapter: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const getTotalStats = () => {
    const totalWords = chapters.reduce((sum, chapter) => sum + chapter.wordCount, 0);
    const avgProgress = chapters.length > 0 
      ? chapters.reduce((sum, chapter) => sum + chapter.progress, 0) / chapters.length 
      : 0;

    return {
      totalWords,
      totalChapters: chapters.length,
      avgProgress: Math.round(avgProgress),
      lastModified: chapters.length > 0 
        ? new Date(Math.max(...chapters.map(c => new Date(c.lastModified)))).toLocaleDateString()
        : 'Never'
    };
  };

  const stats = getTotalStats();

  return (
    <div className="component manuscript-manager">
      <div className="manuscript-header">
        <div className="manuscript-title-section">
          <input
            type="text"
            value={manuscriptTitle}
            onChange={(e) => setManuscriptTitle(e.target.value)}
            className="manuscript-title-input"
            placeholder="Enter manuscript title..."
          />
          <div className="manuscript-actions">
            <button onClick={exportManuscript} className="button secondary small">
              ðŸ“¥ Export
            </button>
            <button onClick={addNewChapter} className="button small">
              âž• New Chapter
            </button>
          </div>
        </div>

        <div className="manuscript-stats">
          <div className="stat-card">
            <span className="stat-number">{stats.totalWords}</span>
            <span className="stat-label">Total Words</span>
          </div>
          <div className="stat-card">
            <span className="stat-number">{stats.totalChapters}</span>
            <span className="stat-label">Chapters</span>
          </div>
          <div className="stat-card">
            <span className="stat-number">{stats.avgProgress}%</span>
            <span className="stat-label">Avg Progress</span>
          </div>
          <div className="stat-card">
            <span className="stat-number">{stats.lastModified}</span>
            <span className="stat-label">Last Modified</span>
          </div>
        </div>
      </div>

      <div className="manuscript-content">
        <div className="chapters-sidebar">
          <div className="sidebar-header">
            <h3>ðŸ“š Chapters</h3>
          </div>

          <ul className="chapters-list">
            {chapters.map((chapter) => (
              <li
                key={chapter.id}
                className={`chapter-item ${activeChapter?.id === chapter.id ? 'active' : ''}`}
                onClick={() => setActiveChapter(chapter)}
              >
                <div className="chapter-info">
                  <span className="chapter-title">{chapter.title}</span>
                  <div className="chapter-actions">
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        analyzeChapter(chapter.id);
                      }}
                      className="analyze-btn"
                      disabled={loading}
                      title="Analyze Chapter"
                    >
                      ðŸ”
                    </button>
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        deleteChapter(chapter.id);
                      }}
                      className="delete-btn"
                      title="Delete Chapter"
                    >
                      ðŸ—‘ï¸
                    </button>
                  </div>
                </div>
                <div className="chapter-meta">
                  {chapter.wordCount} words â€¢ {chapter.progress}% complete
                </div>
                <div className="progress-indicator">
                  <div 
                    className="progress-fill" 
                    style={{ width: `${chapter.progress}%` }}
                  ></div>
                </div>
              </li>
            ))}
          </ul>
        </div>

        <div className="editor-area">
          {activeChapter ? (
            <ChapterEditor
              chapter={activeChapter}
              onUpdate={updateChapter}
              loading={loading}
            />
          ) : (
            <div className="no-chapter-selected">
              <div className="empty-state">
                <h3>ðŸ“ No Chapter Selected</h3>
                <p>Select a chapter from the sidebar or create a new one to start writing.</p>
                <button onClick={addNewChapter} className="button">
                  âž• Create First Chapter
                </button>
              </div>
            </div>
          )}
        </div>
      </div>

      {error && <div className="error-message">{error}</div>}
    </div>
  );
};

const ChapterEditor = ({ chapter, onUpdate, loading }) => {
  const [content, setContent] = useState(chapter.content);
  const [title, setTitle] = useState(chapter.title);
  const [progress, setProgress] = useState(chapter.progress);
  const [notes, setNotes] = useState(chapter.notes || '');
  const [showAnalysis, setShowAnalysis] = useState(false);

  useEffect(() => {
    setContent(chapter.content);
    setTitle(chapter.title);
    setProgress(chapter.progress);
    setNotes(chapter.notes || '');
  }, [chapter]);

  const handleContentChange = (e) => {
    const newContent = e.target.value;
    setContent(newContent);
    onUpdate(chapter.id, { content: newContent });
  };

  const handleTitleChange = (e) => {
    const newTitle = e.target.value;
    setTitle(newTitle);
    onUpdate(chapter.id, { title: newTitle });
  };

  const handleProgressChange = (e) => {
    const newProgress = parseInt(e.target.value);
    setProgress(newProgress);
    onUpdate(chapter.id, { progress: newProgress });
  };

  const handleNotesChange = (e) => {
    const newNotes = e.target.value;
    setNotes(newNotes);
    onUpdate(chapter.id, { notes: newNotes });
  };

  const wordCount = content.split(' ').filter(w => w).length;

  return (
    <div className="chapter-editor">
      <div className="chapter-editor-header">
        <div className="title-section">
          <input
            type="text"
            value={title}
            onChange={handleTitleChange}
            className="chapter-title-input"
            placeholder="Chapter Title..."
          />
          <div className="chapter-stats">
            <span className="word-count">{wordCount} words</span>
            <div className="progress-section">
              <label>Progress: {progress}%</label>
              <input
                type="range"
                min="0"
                max="100"
                value={progress}
                onChange={handleProgressChange}
                className="progress-slider"
              />
            </div>
          </div>
        </div>

        <div className="editor-controls">
          <button
            onClick={() => setShowAnalysis(!showAnalysis)}
            className={`toggle-btn ${showAnalysis ? 'active' : ''}`}
          >
            {showAnalysis ? 'ðŸ“ Editor' : 'ðŸ“Š Analysis'}
          </button>
        </div>
      </div>

      {!showAnalysis ? (
        <div className="editor-content">
          <textarea
            value={content}
            onChange={handleContentChange}
            placeholder="Start writing your chapter here...

ðŸ’¡ Writing Tips:
â€¢ Set a daily word count goal
â€¢ Write consistently, even if it's just 100 words
â€¢ Don't edit while writing your first draft
â€¢ Use the progress slider to track completion
â€¢ Add notes below for plot points or character development"
            className="chapter-textarea"
          />

          <div className="notes-section">
            <label>ðŸ“ Chapter Notes:</label>
            <textarea
              value={notes}
              onChange={handleNotesChange}
              placeholder="Add notes about plot points, character development, or things to remember..."
              className="notes-textarea"
            />
          </div>
        </div>
      ) : (
        <div className="analysis-content">
          {chapter.analysis ? (
            <div className="chapter-analysis">
              <h4>ðŸ“Š Chapter Analysis</h4>
              <div className="analysis-results">
                <div className="analysis-item">
                  <strong>Pacing:</strong> {chapter.analysis.pacing}
                </div>
                <div className="analysis-item">
                  <strong>Tone:</strong> {chapter.analysis.tone}
                </div>
                <div className="analysis-item">
                  <strong>Conflict Level:</strong> {chapter.analysis.conflictLevel}
                </div>
                <div className="analysis-item">
                  <strong>Character Development:</strong> {chapter.analysis.characterDevelopment}
                </div>
              </div>

              {chapter.suggestions && (
                <div className="suggestions-section">
                  <h4>ðŸ’¡ Suggestions</h4>
                  <ul>
                    {chapter.suggestions.map((suggestion, index) => (
                      <li key={index}>{suggestion}</li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          ) : (
            <div className="no-analysis">
              <p>No analysis available. Click the analyze button (ðŸ”) in the chapter list to get AI insights.</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default ManuscriptManager;
